<template><section class='fluent-card' :class='[ strong ? "fluent-acrylic-strong" : "fluent-acrylic", { hoverable, collapsed } ]'><header class='fc-header'><div class='fc-title'><slot name='icon'></slot><span v-if='title'>{{ title }}</span><slot name='title'></slot></div><div class='fc-actions'><slot name='actions'></slot><button v-if='collapsible' class='fc-btn' @click='collapsed=!collapsed'>{{ collapsed ? '展开' : '收起' }}</button></div></header><div v-show='!collapsed' class='fc-body' :class='{ scrollable }' :style='bodyStyle'><slot></slot></div><footer v-if='!collapsed && $slots.footer' class='fc-footer'><slot name='footer'></slot></footer></section></template><script setup lang='ts'>import { computed, ref } from 'vue';const props = defineProps<{ title?: string; strong?: boolean; hoverable?: boolean; collapsible?: boolean; defaultCollapsed?: boolean; scrollable?: boolean; bodyPadding?: number; minBodyHeight?: number; }>();const collapsed = ref(!!props.defaultCollapsed);const bodyStyle = computed(()=>({ padding: `${props.bodyPadding ?? 10}px`, minHeight: props.minBodyHeight ? `${props.minBodyHeight}px` : undefined }));</script><style scoped>.fluent-card{display:flex;flex-direction:column;min-height:0;border-radius:12px;overflow:hidden;box-sizing:border-box;border:1px solid var(--panel-border,rgba(255,255,255,.10));transition:box-shadow .2s ease,transform .2s ease,background .2s ease}.fluent-card.hoverable:hover{box-shadow:0 14px 36px rgba(0,0,0,.5)}.fc-header{display:flex;align-items:center;justify-content:space-between;padding:10px 12px;gap:10px;border-bottom:1px solid rgba(255,255,255,.06)}.fc-title{display:flex;align-items:center;gap:8px;font-weight:600}.fc-actions{display:flex;align-items:center;gap:8px}.fc-btn{border:1px solid rgba(255,255,255,.15);background:transparent;color:#fff;padding:6px 10px;border-radius:10px;cursor:pointer}.fc-btn:hover{background:rgba(255,255,255,.08)}.fc-body{flex:1;min-height:0;overflow:hidden}.fc-body.scrollable{overflow:auto;scrollbar-gutter:stable}.fc-footer{padding:8px 12px;border-top:1px solid rgba(255,255,255,.06);text-align:right;opacity:.9}</style>