<template><aside class='side'><FluentCard title='人员实时检测' strong><DonutChart :data='people' :fill='true'/><template #footer>总人数：{{ totalPeople }}</template></FluentCard><FluentCard title='车辆实时检测' strong><DonutChart :data='cars' :fill='true' :colors="['#FF4E50','#FEBB2E','#37A2DA']"/><template #footer>总车辆：{{ totalCars }}</template></FluentCard><FluentCard title='运营情况分析' strong><RadarChart :indicators='opsIndicators' :values='opsValues' :fill='true'/></FluentCard></aside></template><script setup lang='ts'>import FluentCard from '@/components/FluentCard.vue';import DonutChart from '@/components/DonutChart.vue';import RadarChart from '@/components/RadarChart.vue';const people=[{name:'内部员工',value:100},{name:'临时工',value:40},{name:'访客人员',value:20}];const cars=[{name:'内部车辆',value:50},{name:'集团车辆',value:30},{name:'访客车辆',value:20}];const totalPeople=people.reduce((a,b)=>a+b.value,0);const totalCars=cars.reduce((a,b)=>a+b.value,0);const opsIndicators=[{name:'设备完好率',max:100},{name:'环境监测完善度',max:100},{name:'安保处理效率',max:100},{name:'应急演练完成度',max:100},{name:'风险评估完善',max:100}];const opsValues=[75,71,65,53,70];</script><style scoped>.side{height:100%;display:grid;grid-template-rows:repeat(3,1fr);gap:10px}</style>